import styled from 'styled-components';

export const Container = styled.div`
display: flex;

`

export const LeftSide = styled.div`
  width: 50%;
  heigth: 70px;
  display: flex;

`



export const RigthSide = styled.div`
width: 50%;
height: 70px;
display: flex;

img {
  width: 450px;
  height: 450px;
  margin-left: 100px;
}
`

export const RightSide = styled.div`
  width: 50%;
  height: 70px;
  display: flex;

  img {
    width: 450px;
    height: 450px;
    margin-left: 100px;
  }
`
