import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

export const LeftSide = styled.div`
  width: 50%;
  min-height: 70px;
  display: flex;
  align-items: center;
  padding: 0 20px;
  box-sizing: border-box;
`;

export const RightSide = styled.div`
  width: 50%;
  min-height: 70px;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 0 20px;
  box-sizing: border-box;

  img {
    max-width: 100%;
    height: auto;
    margin-left: auto;
    margin-right: 20px;
  }
`;
